import React, { useState, useEffect} from 'react';
import { View, Text, StyleSheet, TouchableOpacity, TextInput, ScrollView, Image, Animated, KeyboardAvoidingView} from 'react-native';
import Api from '../../Api';

import RNPickerSelect from "react-native-picker-select";

export default function moreBook() {
    
    const [selectedGen, setSelectedGen] = useState([]);
    const [listGenre, setListGenre] = useState([]);
    const [nameField, setNameField] = useState('');
    const [descField, setDescField] = useState('');
    const [authorFiled, setAuthorField] = useState('');
    const [linkFiled, setLinkField] = useState('');
    const [messageEmpty, setMessageEmpty] = useState('none');
    const [sucessMessage, setSucessMessage] = useState('none');
    const [lResult, setlResult] = useState({
        msg: '',
        success: true
    });

    const [offset] = useState(new Animated.ValueXY({ x: 0, y: 150 }));

    const clearMessage = () => {
        setSucessMessage('none'); 
        setMessageEmpty('none');
    };

    const handleAddBookClick = async () => {
        if( nameField != '' && selectedGen != 0 && authorFiled != '') {
            let json = await Api.addBook(nameField, descField, selectedGen, authorFiled, linkFiled);
            if(json) {
                lResult.msg  = json.mensagem;
                setSucessMessage('flex');
            } else {
                alert("Erro: " + json.mensagem);
            }
        } else {
            setMessageEmpty('flex');
        }
    };

    useEffect(() => {
        Animated.spring(offset.y, {
            toValue: 0,
            speed: 10,
            bounciness: 20,
            useNativeDriver: true
        }).start();

        let isFlag = true;
        Api.getGenres().then((response) => {
            if(isFlag){
                if(response.data[0] != null) {
                    setListGenre(response.data);
                }
            }
        }).catch((error) => {
            alert('Erro inesperado, contate o adminstrador');
        });
        return () => { isFlag = false };
    }, []);

    return(
        <View style={styles.background}>
            <ScrollView contentContainerStyle={{ flexGrow: 1, justifyContent: 'flex-start', alignItems: 'center' }}>
                <View style={styles.headerBody}>
                    <Text style={styles.subTitle}>Insira as informaÃ§Ãµes para cadastrar o livro</Text>
                </View>
                <Animated.View style={[ styles.pageBody,  { transform: [ { translateY: offset.y } ] }]}>
                    <View style={styles.inputArea}>
                        <TextInput 
                            style={styles.input} 
                            placeholder="Nome do livro"
                            placeholderTextColor="#000000"
                            value={nameField}
                            onChangeText={t=>setNameField(t)}
                            onFocus={t=>clearMessage()}
                        />
                    </View>
                    <View style={styles.PickerArea}>
                        <RNPickerSelect
                            placeholder={{
                                label: 'Selecione um gÃªnero',
                                value: null,
                            }}
                            placeholderTextColor="#000000"
                            onValueChange={(value) => setSelectedGen(value)}
                            style={styles.picker}
                            items={listGenre.map((item, k) => {
                                return ({ 
                                    label: item.GEN_NOME, value: item.GEN_ID 
                                })
                            })}
                        />
                    </View>
                    <View style={styles.inputArea}>
                        <TextInput 
                            style={styles.input} 
                            placeholder="Autor"
                            placeholderTextColor="#000000"
                            value={authorFiled}
                            onChangeText={t=>setAuthorField(t)}
                            onFocus={t=>clearMessage()}
                        />
                    </View>
                    <View style={styles.inputArea}>
                        <TextInput 
                            style={styles.input} 
                            placeholder="Link PDF"
                            placeholderTextColor="#000000"
                            value={linkFiled}
                            onChangeText={t=>setLinkField(t)}
                        />
                    </View>
                    <View style={styles.textArea}>
                        <TextInput 
                            multiline={true} 
                            numberOfLines={5} 
                            style={styles.input} 
                            placeholder="DescriÃ§Ã£o"
                            placeholderTextColor="#000000"
                            value={descField}
                            onChangeText={t=>setDescField(t)}
                        />
                    </View>
                    <View style={styles.messageValid}>
                        <Text style={{display: messageEmpty, color: '#FF0000', }}>
                        Preencha todos os campos!
                        </Text>
                        <Text style={{display: sucessMessage, color: '#17F1A1', }}>
                        {lResult.msg}
                        </Text>
                    </View>
                    <TouchableOpacity onPress={handleAddBookClick} style={styles.registerButton}>
                        <Text style={styles.registerText}>Cadastrar</Text>
                    </TouchableOpacity>
                </Animated.View>
                </ScrollView>
        </View>
    );
};

const styles = StyleSheet.create({
    fixKeyboard: {
        bottom: 0,
        left: 0,
        flexDirection: 'row',
        paddingTop: 80,
        alignContent: 'center'
    },
    picker: {
        flex: 1,
        fontSize: 16,
        color: '#000000',
        marginLeft: 5
    },
    background: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: '#FFFFFF',
    },
    headerBody: {
        height: 100,
        width: 400,
        alignItems: 'center',
        justifyContent: 'space-around',
        marginBottom: 10
    },
    subTitle: {
        fontSize: 24,
        color: '#000',
        fontWeight: 'bold'
    },
    PickerArea: {
        width: 350,
        height: 45,
        backgroundColor: '#F5F5F5',
        paddingLeft: 15,
        borderRadius: 10,
        alignItems: 'center',
        justifyContent: 'center',
        borderWidth: 2,
        borderColor: '#000000'
    },
    inputArea: {
        width: 350,
        height: 45,
        backgroundColor: '#F5F5F5',
        flexDirection: 'row',
        borderRadius: 10,
        paddingLeft: 15,
        alignItems: 'center',
        borderWidth: 2,
        borderColor: '#000000'
    },
    input: {
        flex: 1,
        fontSize: 16,
        color: '#000000',
        marginLeft: 5
    },
    textArea: {
        width: 350,
        height: 150,
        backgroundColor: '#F5F5F5',
        flexDirection: 'row',
        borderRadius: 10,
        paddingLeft: 15,
        alignItems: 'center',
        borderWidth: 2,
        borderColor: '#000000'
    },
    registerButton: {
        height: 50,
        width: 350,
        backgroundColor: '#17F1A1',
        borderRadius: 10,
        justifyContent: 'center',
        alignItems: 'center'
    },
    registerText: {
        fontSize: 18,
        color: '#000000',
        fontWeight: 'bold'
    },
    pageBody: {
        width: 400,
        height: 550,
        alignItems: 'center',
        justifyContent: 'space-around'
    },
    messageValid: {
        width: 350,
        height: 30,
        justifyContent: 'center',
        alignItems: 'center'
    },
});
